url.value:
@{concat('https://accounts.accesscontrol.windows.net/'
,json(activity('lkp metadata of Source to Landing from SPO').output.value[0].SourceParameterJSON).pl_par_Tenant_id
,'/tokens/OAuth/2'
)}

@{concat(
    concat(
        json(activity('lkp metadata of Source to Landing from SPO').output.value[0].SourceParameterJSON).pl_par_KeyVault_URL
    , '/secrets/'
        )
	,
    json(activity('lkp metadata of Source to Landing from SPO').output.value[0].SourceParameterJSON).pl_par_keyVault_SecretsName 
    , '?api-version=7.0'
)}

ds_par_dstpath.value:
@concat('landing/',
replace(replace(activity('lkp metadata of Source to Landing from SPO').output.value[0].LandingPath,'/<<yyyy-MM-dd>>', '')
,'<<fileversion>>',activity('lkp metadata of Source to Landing from SPO').output.value[0].SourceFileVersion)
)

ds_par_dst_filename.value:
@{

        replace(
            concat(
                'rundate='
                , formatDateTime(convertTimeZone(utcNow(),'UTC' ,'Eastern Standard Time' ),'yyyy-MM-dd')
                , item().path
                , '/'
                , item().Name

            )
            , '/sites', ''
        )

}
